import React, { useState } from "react";
import { Upload, X, Sparkles, Zap, AlertCircle } from "lucide-react";

const ImageIcon = ({ size = 24, className = "" }) => (
  <svg width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className={className}>
    <rect x="3" y="3" width="18" height="18" rx="2" ry="2"/>
    <circle cx="8.5" cy="8.5" r="1.5"/>
    <polyline points="21 15 16 10 5 21"/>
  </svg>
);

const EnhancedVQAForm = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fromCache, setFromCache] = useState(false);
  const [fileName, setFileName] = useState("");
  const [dragActive, setDragActive] = useState(false);

  const quickQuestions = [
    "What is in this image?",
    "Describe the main objects",
    "What colors are prominent?",
    "What is the setting or location?",
    "How many people are visible?",
    "What activity is happening?"
  ];

  const processFile = (selectedFile) => {
    setFile(selectedFile);
    setFileName(selectedFile?.name || "");
    setAnswer("");
    setConfidence(null);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(selectedFile);
    } else {
      setPreview(null);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      processFile(e.dataTransfer.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !question) return alert("Upload image & enter question");

    const formData = new FormData();
    formData.append("image", file);
    formData.append("question", question);

    setLoading(true);
    setAnswer("");
    setConfidence(null);

    try {
      const res = await fetch("http://localhost:8000/vqa/", {
        method: "POST",
        body: formData
      });

      const data = await res.json();
      setAnswer(data.answer);
      setConfidence(data.confidence ?? null);
      setFromCache(data.from_cache || false);
    } catch {
      setAnswer("Error processing request. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile(null);
    setPreview(null);
    setQuestion("");
    setAnswer("");
    setConfidence(null);
    setFromCache(false);
    setFileName("");
  };

  const getConfidenceLevel = (c) => (c >= 0.8 ? "high" : c >= 0.5 ? "medium" : "low");
  const getConfidenceColor = (c) => (c >= 0.8 ? "#10b981" : c >= 0.5 ? "#f59e0b" : "#ef4444");

  return (
    <div className="container">
      {/* Header */}
      <div className="header">
        <div className="header-content">
          <div className="header-icon">
            <Sparkles size={28} />
          </div>
          <div>
            <h1>Visual Question Answering</h1>
            <p className="subtitle">Upload an image and ask questions about it using AI</p>
          </div>
        </div>
      </div>

      {/* Main Layout */}
      <div className="vqa-layout">
        {/* Left Side - Form */}
        <div className="vqa-form">
          <div className="form-container">
            {/* Upload Area */}
            <div 
              className={`upload-zone ${dragActive ? "drag-active" : ""} ${preview ? "has-preview" : ""}`}
              onDragEnter={handleDrag}
              onDragLeave={handleDrag}
              onDragOver={handleDrag}
              onDrop={handleDrop}
            >
              <input 
                type="file" 
                accept="image/*" 
                onChange={(e) => processFile(e.target.files[0])}
                id="file-input"
                className="file-input"
              />
              <label htmlFor="file-input" className="upload-label">
                {preview ? (
                  <div className="upload-success">
                    <ImageIcon size={24} className="icon-success" />
                    <span className="file-name">{fileName}</span>
                    <button 
                      type="button" 
                      className="change-btn"
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        document.getElementById('file-input').click();
                      }}
                    >
                      Change Image
                    </button>
                  </div>
                ) : (
                  <div className="upload-prompt">
                    <Upload size={32} className="upload-icon" />
                    <span className="upload-text">Click to upload or drag and drop</span>
                    <span className="upload-hint">PNG, JPG, WEBP up to 10MB</span>
                  </div>
                )}
              </label>
            </div>

            {/* Question Input */}
            <div className="question-section">
              <label className="input-label">Your Question</label>
              <textarea
                placeholder="What would you like to know about this image?"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                className="question-input"
                rows="3"
              />
            </div>

            {/* Quick Questions */}
            {!question && (
              <div className="quick-questions">
                <label className="input-label">Quick Questions</label>
                <div className="quick-grid">
                  {quickQuestions.map((q, i) => (
                    <button 
                      key={i} 
                      type="button" 
                      className="quick-btn"
                      onClick={() => setQuestion(q)}
                    >
                      <Zap size={14} />
                      {q}
                    </button>
                  ))}
                </div>
              </div>
            )}

            {/* Action Buttons */}
            <div className="action-buttons">
              <button 
                type="button"
                className="submit-btn"
                disabled={loading || !file || !question}
                onClick={handleSubmit}
              >
                {loading ? (
                  <>
                    <div className="spinner" />
                    Analyzing...
                  </>
                ) : (
                  <>
                    <Sparkles size={18} />
                    Ask Question
                  </>
                )}
              </button>
              {(file || question) && (
                <button 
                  type="button"
                  className="reset-btn"
                  onClick={handleReset}
                >
                  <X size={18} />
                  Reset
                </button>
              )}
            </div>
          </div>

          {/* Answer Section */}
          {answer && !loading && (
            <div className="result-card">
              <div className="result-header">
                <span className="result-label">Answer</span>
                {confidence !== null && (
                  <div className="confidence-badge" style={{ background: `${getConfidenceColor(confidence)}20`, color: getConfidenceColor(confidence) }}>
                    <div 
                      className="confidence-bar" 
                      style={{ 
                        width: `${confidence * 100}%`,
                        background: getConfidenceColor(confidence)
                      }}
                    />
                    <span className="confidence-text">{(confidence * 100).toFixed(0)}% confident</span>
                  </div>
                )}
              </div>
              <p className="answer-text">{answer}</p>
              {fromCache && (
                <div className="cache-notice">
                  <AlertCircle size={14} />
                  <span>Retrieved from cache</span>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Right Side - Preview */}
        <div className="vqa-preview">
          <div className="preview-container">
            <div className="preview-header">
              <ImageIcon size={18} />
              <span>Image Preview</span>
            </div>
            {preview ? (
              <div className="image-preview">
                <img src={preview} alt="Preview" />
                <button 
                  className="remove-btn"
                  onClick={handleReset}
                  type="button"
                >
                  <X size={16} />
                </button>
              </div>
            ) : (
              <div className="preview-placeholder">
                <ImageIcon size={48} className="placeholder-icon" />
                <p className="placeholder-text">No image uploaded yet</p>
                <p className="placeholder-hint">Upload an image to see preview</p>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Styles */}
      <style>{`
        * {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
        }

        .container {
          min-height: 100vh;
