import { useState } from "react";

export default function VQAForm() {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [confidence, setConfidence] = useState(null);

  // Handle file selection and preview
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(selectedFile);
    } else {
      setPreview(null);
    }
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Example: mock answer and confidence
    // Replace this with your VQA API call
    if (question && file) {
      setAnswer("This is the predicted answer.");
      setConfidence(0.85); // mock confidence
    } else {
      setAnswer("");
      setConfidence(null);
    }
  };

  return (
    <div className="container">
      <h1>VQA App</h1>
      <form className="vqa-form" onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <input
          type="text"
          placeholder="Enter your question"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button type="submit">Ask</button>
      </form>

      {/* Image preview */}
      {preview && (
        <div className="image-preview">
          <img src={preview} alt="Preview" />
        </div>
      )}

      {/* Answer card */}
      {answer && (
        <div className="result-card">
          <div className="answer">{answer}</div>
          {confidence !== null && (
            <div className="confidence-container">
              <span
                className={`confidence-badge ${
                  confidence >= 0.8
                    ? "high"
                    : confidence >= 0.5
                    ? "medium"
                    : "low"
                }`}
              >
                {Math.round(confidence * 100)}% confident
              </span>
            </div>
          )}
        </div>
      )}
    </div>
  );
}
