import React, { useState } from "react";

const EnhancedVQAForm = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fromCache, setFromCache] = useState(false);
  const [fileName, setFileName] = useState("");
  const [dragActive, setDragActive] = useState(false);
  const [imageDimensions, setImageDimensions] = useState(null);
  const [fileType, setFileType] = useState("");
  const [uploadTime, setUploadTime] = useState(null);

  const quickQuestions = [
    "What is in this image?",
    "Describe the main objects",
    "What colors are prominent?",
    "What is the setting or location?",
    "How many people are visible?",
    "What activity is happening?"
  ];

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    processFile(selectedFile);
  };

  const processFile = (selectedFile) => {
    setFile(selectedFile);
    setFileName(selectedFile?.name || "");
    setFileType(selectedFile?.type || "");
    setUploadTime(new Date());

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(selectedFile);
    } else {
      setPreview(null);
      setFileName("");
      setFileType("");
      setUploadTime(null);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      processFile(e.dataTransfer.files[0]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file || !question) {
      return alert("Please upload an image and enter a question.");
    }

    const formData = new FormData();
    formData.append("image", file);
    formData.append("question", question);

    setLoading(true);
    setAnswer("");
    setConfidence(null);
    setFromCache(false);

    try {
      const res = await fetch("http://localhost:8000/vqa/", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Server error: ${res.status} - ${text}`);
      }

      const data = await res.json();
      setAnswer(data.answer);
      setConfidence(data.confidence ?? null);
      setFromCache(data.from_cache || false);
    } catch (err) {
      console.error(err);
      setAnswer("Error processing request. Please try again.");
      setConfidence(null);
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile(null);
    setPreview(null);
    setQuestion("");
    setAnswer("");
    setConfidence(null);
    setFromCache(false);
    setFileName("");
    setImageDimensions(null);
    setFileType("");
    setUploadTime(null);
  };

  const getConfidenceLevel = (conf) => {
    if (conf >= 0.8) return "high";
    if (conf >= 0.5) return "medium";
    return "low";
  };

  const getConfidenceEmoji = (conf) => {
    if (conf >= 0.8) return "âœ“";
    if (conf >= 0.5) return "~";
    return "!";
  };

  const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + " B";
    if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(2) + " KB";
    return (bytes / (1024 * 1024)).toFixed(2) + " MB";
  };

  const getOrientation = () => {
    if (!imageDimensions) return "";
    const { width, height } = imageDimensions;
    if (width > height) return "Landscape";
    if (width < height) return "Portrait";
    return "Square";
  };

  return (
    <div style={{ 
      display: "flex", 
      gap: "2rem", 
      alignItems: "stretch",
      maxWidth: "1400px",
      margin: "40px auto",
      padding: "0 20px",
      minHeight: "calc(100vh - 80px)"
    }}>
      {/* Main Form Container */}
      <div style={{ 
        flex: 1,
        minWidth: "400px",
        background: "white",
        padding: "2.5rem",
        borderRadius: "16px",
        boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1)",
        height: "fit-content"
      }}>
        <h1 style={{
          fontSize: "2rem",
          fontWeight: "700",
          color: "#1f2937",
          marginBottom: "0.5rem",
          textAlign: "center"
        }}>ðŸ” Visual Question Answering</h1>
        
        <p style={{ 
          textAlign: "center", 
          color: "#6b7280", 
          marginBottom: "2rem", 
          fontSize: "1.05rem" 
        }}>
          Upload an image and ask questions about it â€” powered by AI
        </p>

        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "1.25rem" }}>
          {/* File Input */}
          <div
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
            style={{
              border: dragActive ? "3px dashed #6366f1" : "2px dashed #e5e7eb",
              backgroundColor: dragActive ? "#eef2ff" : "#f9fafb",
              borderRadius: "12px",
              transition: "all 0.3s ease"
            }}
          >
            <input
              type="file"
              id="file-upload"
              accept="image/*"
              onChange={handleFileChange}
              style={{ display: "none" }}
            />
            <label 
              htmlFor="file-upload" 
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                gap: "0.75rem",
                padding: "1.5rem",
                cursor: "pointer",
                fontWeight: "500",
                color: dragActive ? "#6366f1" : "#6b7280"
              }}
            >
              <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
              {fileName ? (
                <span style={{ display: "flex", flexDirection: "column", alignItems: "center", gap: "0.25rem" }}>
                  <strong>{fileName}</strong>
                  <span style={{ fontSize: "0.875rem", opacity: 0.7 }}>Click to change</span>
                </span>
              ) : (
                <span>
                  {dragActive ? "Drop your image here" : "Choose an image or drag & drop"}
                </span>
              )}
            </label>
          </div>

          {/* Question Input */}
          <div>
            <label style={{
              display: "block",
              marginBottom: "0.5rem",
              fontWeight: "600",
              color: "#374151",
              fontSize: "1rem"
            }}>
              ðŸ’¬ Your Question
            </label>
            <textarea
              placeholder="What would you like to know about this image?"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              style={{
                width: "100%",
                minHeight: "100px",
                padding: "1rem",
                fontSize: "1rem",
                border: "2px solid #e5e7eb",
                borderRadius: "12px",
                resize: "vertical",
                fontFamily: "inherit",
                transition: "all 0.3s ease"
              }}
              onFocus={(e) => {
                e.target.style.borderColor = "#6366f1";
                e.target.style.boxShadow = "0 0 0 4px #eef2ff";
              }}
              onBlur={(e) => {
                e.target.style.borderColor = "#e5e7eb";
                e.target.style.boxShadow = "none";
              }}
            />
          </div>

          {/* Quick Questions */}
          {!question && (
            <div>
              <label style={{
                display: "block",
                marginBottom: "0.5rem",
                fontWeight: "600",
                color: "#374151",
                fontSize: "0.95rem"
              }}>
                ðŸ’¡ Quick questions
              </label>
              <div style={{
                display: "flex",
                flexWrap: "wrap",
                gap: "0.5rem"
              }}>
                {quickQuestions.map((q, idx) => (
                  <button
                    key={idx}
                    type="button"
                    onClick={() => setQuestion(q)}
                    style={{
                      padding: "0.5rem 1rem",
                      fontSize: "0.875rem",
                      background: "linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)",
                      color: "#374151",
                      border: "1px solid #d1d5db",
                      borderRadius: "9999px",
                      cursor: "pointer",
                      transition: "all 0.3s ease",
                      fontWeight: "500"
                    }}
                    onMouseEnter={(e) => {
                      e.target.style.background = "linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)";
                      e.target.style.color = "white";
                      e.target.style.transform = "translateY(-2px)";
                    }}
                    onMouseLeave={(e) => {
                      e.target.style.background = "linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)";
                      e.target.style.color = "#374151";
                      e.target.style.transform = "translateY(0)";
                    }}
                  >
                    {q}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div style={{ display: "flex", gap: "1rem", marginTop: "0.5rem" }}>
            <button 
              type="submit" 
              disabled={loading || !file || !question} 
              style={{ 
                flex: 1,
                padding: "0.875rem 1.5rem",
                fontSize: "1rem",
                fontWeight: "600",
                border: "none",
                borderRadius: "12px",
                cursor: loading || !file || !question ? "not-allowed" : "pointer",
                background: "linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)",
                color: "white",
                opacity: (loading || !file || !question) ? 0.5 : 1,
                transition: "all 0.3s ease"
              }}
            >
              {loading ? (
                <span style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: "0.5rem" }}>
                  <span style={{
                    border: "2px solid rgba(255,255,255,0.3)",
                    borderTop: "2px solid white",
                    borderRadius: "50%",
                    width: "16px",
                    height: "16px",
                    animation: "spin 0.8s linear infinite"
                  }}></span>
                  Processing...
                </span>
              ) : "ðŸš€ Ask Question"}
            </button>
            {(file || question) && (
              <button
                type="button"
                onClick={handleReset}
                style={{
                  background: "linear-gradient(135deg, #6b7280 0%, #4b5563 100%)",
                  color: "white",
                  padding: "0.875rem 1.5rem",
                  fontSize: "1rem",
                  fontWeight: "600",
                  border: "none",
                  borderRadius: "12px",
                  cursor: "pointer",
                  transition: "all 0.3s ease"
                }}
              >
                ðŸ”„ Reset
              </button>
            )}
          </div>
        </form>

        {/* Loader */}
        {loading && (
          <div style={{ marginTop: "2rem", textAlign: "center", padding: "2rem" }}>
            <div style={{
              border: "4px solid #f3f4f6",
              borderTop: "4px solid #6366f1",
              borderRadius: "50%",
              width: "48px",
              height: "48px",
              animation: "spin 0.8s linear infinite",
              margin: "0 auto 1rem"
            }}></div>
            <p style={{ fontSize: "1rem", color: "#6b7280", fontWeight: "500" }}>
              ðŸ¤– AI is analyzing your image...
            </p>
            <p style={{ fontSize: "0.9rem", color: "#9ca3af", marginTop: "0.5rem" }}>
              This usually takes 5-10 seconds
            </p>
          </div>
        )}

        {/* Answer Display */}
        {!loading && answer && (
          <div style={{
            marginTop: "2rem",
            padding: "1.75rem",
            borderRadius: "12px",
            background: "linear-gradient(135deg, #f6f8fb 0%, #ffffff 100%)",
            boxShadow: "0 4px 6px -1px rgba(0, 0, 0, 0.1)",
            borderLeft: "4px solid #10b981"
          }}>
            {fromCache && (
              <div style={{
                backgroundColor: "#fef3c7",
                color: "#92400e",
                padding: "0.75rem 1rem",
                borderRadius: "8px",
                fontSize: "0.9rem",
                marginBottom: "1.25rem",
                display: "flex",
                alignItems: "center",
                gap: "0.5rem",
                fontWeight: "600",
                border: "1px solid #fde68a"
              }}>
                âš¡ Retrieved from cache (instant result!)
              </div>
            )}
            
            <div style={{
              display: "flex",
              alignItems: "start",
              gap: "1rem",
              marginBottom: "1rem"
            }}>
              <div style={{ fontSize: "2rem", flexShrink: 0 }}>ðŸ’¬</div>
              <div style={{
                fontSize: "1.25rem",
                fontWeight: "600",
                color: "#1f2937",
                lineHeight: "1.6"
              }}>
                {answer}
              </div>
            </div>
            
            {confidence !== null && (
              <div style={{ display: "flex", justifyContent: "flex-end", marginTop: "1rem" }}>
                <span style={{
                  padding: "0.5rem 1rem",
                  borderRadius: "9999px",
                  fontSize: "0.875rem",
                  fontWeight: "600",
                  color: "white",
                  display: "inline-flex",
                  alignItems: "center",
                  gap: "0.5rem",
                  background: confidence >= 0.8 ? "linear-gradient(135deg, #10b981 0%, #059669 100%)" :
                             confidence >= 0.5 ? "linear-gradient(135deg, #f59e0b 0%, #d97706 100%)" :
                             "linear-gradient(135deg, #ef4444 0%, #dc2626 100%)"
                }}>
                  {getConfidenceEmoji(confidence)} {(confidence * 100).toFixed(1)}% Confident
                </span>
              </div>
            )}

            <div style={{
              marginTop: "1.5rem",
              paddingTop: "1.5rem",
              borderTop: "1px solid #e5e7eb",
              display: "flex",
              justifyContent: "center"
            }}>
              <button
                type="button"
                onClick={handleReset}
                style={{
                  background: "linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)",
                  color: "white",
                  padding: "0.75rem 1.5rem",
                  fontSize: "0.9rem",
                  fontWeight: "600",
                  border: "none",
                  borderRadius: "12px",
                  cursor: "pointer"
                }}
              >
                Ask Another Question
              </button>
            </div>
          </div>
        )}

        {/* Tips */}
        {!preview && !loading && !answer && (
          <div style={{
            marginTop: "2rem",
            padding: "1.75rem",
            background: "linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)",
            borderRadius: "12px",
            border: "2px solid #bae6fd"
          }}>
            <h3 style={{ 
              color: "#0c4a6e", 
              marginBottom: "1rem", 
              fontSize: "1.15rem",
              display: "flex",
              alignItems: "center",
              gap: "0.5rem"
            }}>
              ðŸ’¡ Tips for Better Results
            </h3>
            <ul style={{
              color: "#0e7490",
              paddingLeft: "1.5rem",
              lineHeight: "1.9",
              margin: 0
            }}>
              <li><strong>Clear images work best:</strong> Use well-lit, focused photos</li>
              <li><strong>Be specific:</strong> Ask direct questions about visible elements</li>
              <li><strong>Examples:</strong> "What color is the car?" or "How many dogs are in the image?"</li>
              <li><strong>Avoid abstract questions:</strong> Focus on what can be seen</li>
            </ul>
          </div>
        )}
      </div>

      {/* Image Preview Panel - Only shown when image is uploaded */}
      {preview && (
        <div style={{
          flex: 2,
          minWidth: "600px",
          background: "white",
          borderRadius: "16px",
          padding: "2rem",
          boxShadow: "0 20px 25px -5px rgba(0, 0, 0, 0.1)",
          display: "flex",
          flexDirection: "column",
          gap: "1.5rem",
          position: "sticky",
          top: "100px",
          height: "fit-content",
          maxHeight: "calc(100vh - 120px)",
          overflow: "auto",
          animation: "fadeIn 0.5s ease"
        }}>
          {/* Header */}
          <div style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            paddingBottom: "1rem",
            borderBottom: "2px solid #e5e7eb"
          }}>
            <h2 style={{
              fontSize: "1.5rem",
              fontWeight: "700",
              color: "#1f2937",
              margin: 0,
              display: "flex",
              alignItems: "center",
              gap: "0.5rem"
            }}>
              ðŸ–¼ï¸ Image Preview
            </h2>
            <button
              onClick={() => {
                setFile(null);
                setPreview(null);
                setFileName("");
                setImageDimensions(null);
                setFileType("");
                setUploadTime(null);
              }}
              style={{
                background: "linear-gradient(135deg, #ef4444 0%, #dc2626 100%)",
                padding: "0.5rem 1rem",
                fontSize: "0.875rem",
                borderRadius: "8px",
                border: "none",
                color: "white",
                cursor: "pointer",
                fontWeight: "600",
                transition: "all 0.3s ease"
              }}
            >
              âœ• Remove
            </button>
          </div>

          {/* Image Container */}
          <div style={{
            position: "relative",
            background: "#f9fafb",
            borderRadius: "12px",
            overflow: "hidden",
            border: "2px solid #e5e7eb",
            minHeight: "300px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}>
            <img
              src={preview}
              alt="Preview"
              style={{ 
                width: "100%",
                height: "auto",
                maxHeight: "500px",
                objectFit: "contain",
                display: "block"
              }}
              onLoad={(e) => {
                const { naturalWidth, naturalHeight } = e.target;
                setImageDimensions({ width: naturalWidth, height: naturalHeight });
              }}
            />
          </div>

          {/* Image Details */}
          <div style={{
            background: "linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)",
            padding: "1.5rem",
            borderRadius: "12px",
            border: "2px solid #bae6fd"
          }}>
            <h3 style={{
              fontSize: "1.1rem",
              fontWeight: "700",
              color: "#0c4a6e",
              marginBottom: "1rem",
              display: "flex",
              alignItems: "center",
              gap: "0.5rem"
            }}>
              ðŸ“Š Image Details
            </h3>
            
            <div style={{
              display: "grid",
              gridTemplateColumns: "repeat(2, 1fr)",
              gap: "1rem"
            }}>
              {/* File Name */}
              <div style={{
                gridColumn: "1 / -1",
                padding: "0.75rem",
                background: "white",
                borderRadius: "8px",
                border: "1px solid #bae6fd"
              }}>
                <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                  FILE NAME
                </div>
                <div style={{ 
                  fontSize: "0.95rem", 
                  color: "#0c4a6e", 
                  fontWeight: "600",
                  wordBreak: "break-word"
                }}>
                  {fileName}
                </div>
              </div>

              {/* File Size */}
              <div style={{
                padding: "0.75rem",
                background: "white",
                borderRadius: "8px",
                border: "1px solid #bae6fd"
              }}>
                <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                  FILE SIZE
                </div>
                <div style={{ fontSize: "1.1rem", color: "#0c4a6e", fontWeight: "700" }}>
                  {formatFileSize(file.size)}
                </div>
                <div style={{ fontSize: "0.7rem", color: "#0e7490", marginTop: "0.25rem" }}>
                  ({file.size.toLocaleString()} bytes)
                </div>
              </div>

              {/* File Type */}
              <div style={{
                padding: "0.75rem",
                background: "white",
                borderRadius: "8px",
                border: "1px solid #bae6fd"
              }}>
                <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                  FILE TYPE
                </div>
                <div style={{ fontSize: "1.1rem", color: "#0c4a6e", fontWeight: "700", textTransform: "uppercase" }}>
                  {fileType.split('/')[1] || 'Unknown'}
                </div>
                <div style={{ fontSize: "0.7rem", color: "#0e7490", marginTop: "0.25rem" }}>
                  {fileType}
                </div>
              </div>

              {/* Dimensions */}
              {imageDimensions && (
                <>
                  <div style={{
                    padding: "0.75rem",
                    background: "white",
                    borderRadius: "8px",
                    border: "1px solid #bae6fd"
                  }}>
                    <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                      DIMENSIONS
                    </div>
                    <div style={{ fontSize: "1.1rem", color: "#0c4a6e", fontWeight: "700" }}>
                      {imageDimensions.width} Ã— {imageDimensions.height}
                    </div>
                    <div style={{ fontSize: "0.7rem", color: "#0e7490", marginTop: "0.25rem" }}>
                      {(imageDimensions.width * imageDimensions.height / 1000000).toFixed(2)}MP
                    </div>
                  </div>

                  <div style={{
                    padding: "0.75rem",
                    background: "white",
                    borderRadius: "8px",
                    border: "1px solid #bae6fd"
                  }}>
                    <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                      ASPECT RATIO
                    </div>
                    <div style={{ fontSize: "1.1rem", color: "#0c4a6e", fontWeight: "700" }}>
                      {(imageDimensions.width / imageDimensions.height).toFixed(2)}:1
                    </div>
                    <div style={{ fontSize: "0.7rem", color: "#0e7490", marginTop: "0.25rem" }}>
                      {getOrientation()}
                    </div>
                  </div>
                </>
              )}

              {/* Upload Time */}
              <div style={{
                gridColumn: "1 / -1",
                padding: "0.75rem",
                background: "white",
                borderRadius: "8px",
                border: "1px solid #bae6fd"
              }}>
                <div style={{ fontSize: "0.75rem", color: "#0e7490", fontWeight: "600", marginBottom: "0.25rem" }}>
                  UPLOADED
                </div>
                <div style={{ fontSize: "0.95rem", color: "#0c4a6e", fontWeight: "600" }}>
                  {uploadTime?.toLocaleString()}
                </div>
              </div>
            </div>
          </div>

          {/* Status Indicator */}
          <div style={{
            padding: "1rem",
            background: "linear-gradient(135deg, #dcfce7 0%, #bbf7d0 100%)",
            borderRadius: "12px",
            border: "2px solid #86efac",
            display: "flex",
            alignItems: "center",
            gap: "0.75rem"
          }}>
            <div style={{
              width: "40px",
              height: "40px",
              borderRadius: "50%",
              background: "linear-gradient(135deg, #10b981 0%, #059669 100%)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: "1.25rem",
              flexShrink: 0,
              color: "white"
            }}>
              âœ“
            </div>
            <div>
              <div style={{ fontWeight: "700", color: "#065f46", fontSize: "1rem" }}>
                Ready for Analysis
              </div>
              <div style={{ fontSize: "0.875rem", color: "#047857", marginTop: "0.125rem" }}>
                Your image has been successfully uploaded and is ready for questioning
              </div>
            </div>
          </div>
        </div>
      )}

      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        @media (max-width: 1200px) {
          body > div > div {
            flex-direction: column !important;
          }
          body > div > div > div {
            min-width: auto !important;
            width: 100% !important;
          }
        }
      `}</style>
    </div>
  );
};

export default EnhancedVQAForm;
