import React, { useEffect, useState } from 'react';
import '../Home.css';

const Home = () => {
  const [stats, setStats] = useState({
    totalImages: 1234,
    completedImages: 1180,
    totalViews: 5678,
    totalDownloads: 890
  });
  const [isVisible, setIsVisible] = useState({});

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible((prev) => ({ ...prev, [entry.target.id]: true }));
          }
        });
      },
      { threshold: 0.1 }
    );

    document.querySelectorAll('[id^="section-"]').forEach((el) => {
      observer.observe(el);
    });

    return () => observer.disconnect();
  }, []);

  const features = [
    {
      icon: "üîç",
      title: "Visual Q&A",
      description: "Upload images and get intelligent answers. Our AI understands context and provides detailed responses.",
      gradient: "from-indigo-500 to-purple-600",
      bgGradient: "from-indigo-50 to-purple-50"
    },
    {
      icon: "üé®",
      title: "AI Image Generator",
      description: "Turn text into stunning visuals. Create art, designs, and illustrations with simple descriptions.",
      gradient: "from-purple-500 to-pink-600",
      bgGradient: "from-purple-50 to-pink-50"
    },
    {
      icon: "üñºÔ∏è",
      title: "Smart Gallery",
      description: "Organize and manage your creations. Search, filter, and access your AI-generated content anytime.",
      gradient: "from-emerald-500 to-teal-600",
      bgGradient: "from-emerald-50 to-teal-50"
    }
  ];

  const steps = [
    { icon: "üì§", title: "Upload or Describe", desc: "Start with an image or text prompt" },
    { icon: "ü§ñ", title: "AI Magic", desc: "Advanced models process your request" },
    { icon: "‚ú®", title: "Get Results", desc: "Instant answers or stunning images" }
  ];

  const useCases = [
    { icon: "üìö", title: "Education", desc: "Analyze diagrams and create learning materials" },
    { icon: "üé≠", title: "Creative Design", desc: "Generate unique artwork and concepts" },
    { icon: "üî¨", title: "Research", desc: "Visual analysis for scientific work" },
    { icon: "üíº", title: "Business", desc: "Marketing visuals and product analysis" }
  ];

  return (
    <div className="min-h-screen bg-gradient-to-b from-white via-gray-50 to-white">
      {/* Enhanced Hero Section */}
      <section className="relative min-h-screen flex items-center justify-center overflow-hidden bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-500">
        {/* Animated background elements */}
        <div className="absolute inset-0 overflow-hidden">
          <div className="absolute -top-1/2 -left-1/2 w-full h-full bg-gradient-to-br from-blue-400/20 to-transparent rounded-full blur-3xl animate-pulse"></div>
          <div className="absolute -bottom-1/2 -right-1/2 w-full h-full bg-gradient-to-tl from-pink-400/20 to-transparent rounded-full blur-3xl animate-pulse delay-1000"></div>
          {[...Array(20)].map((_, i) => (
            <div
              key={i}
              className="absolute bg-white/10 rounded-full animate-float"
              style={{
                width: Math.random() * 100 + 50 + 'px',
                height: Math.random() * 100 + 50 + 'px',
                left: Math.random() * 100 + '%',
                top: Math.random() * 100 + '%',
                animationDelay: Math.random() * 5 + 's',
                animationDuration: Math.random() * 10 + 10 + 's'
              }}
            ></div>
          ))}
        </div>

        <div className="relative z-10 max-w-6xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          {/* Badge */}
          <div className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-white/20 backdrop-blur-lg border border-white/30 mb-8 animate-slide-down">
            <span className="w-2 h-2 bg-emerald-400 rounded-full animate-pulse"></span>
            <span className="text-white text-sm font-semibold">Powered by Advanced AI Models</span>
          </div>

          {/* Main Heading */}
          <h1 className="text-5xl sm:text-6xl lg:text-7xl font-black text-white mb-6 animate-fade-in leading-tight">
            Welcome to
            <span className="block bg-gradient-to-r from-yellow-300 via-orange-300 to-yellow-400 bg-clip-text text-transparent animate-shimmer">
              VisionFusion AI
            </span>
          </h1>

          <p className="text-2xl sm:text-3xl font-bold text-white/95 mb-4 animate-fade-in-delay-1">
            See It. Ask It. Create It.
          </p>

          <p className="text-lg sm:text-xl text-white/90 max-w-3xl mx-auto mb-10 animate-fade-in-delay-2 leading-relaxed">
            Experience cutting-edge multimodal AI with Visual Question Answering and Text-to-Image generation. 
            Transform how you interact with visual content.
          </p>

          {/* CTA Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center mb-12 animate-fade-in-delay-3">
            <button className="group px-8 py-4 bg-white text-indigo-600 rounded-2xl font-bold text-lg shadow-2xl hover:shadow-indigo-500/50 transform hover:-translate-y-1 transition-all duration-300 flex items-center justify-center gap-2">
              <span className="text-2xl group-hover:scale-110 transition-transform">üöÄ</span>
              Get Started Free
              <svg className="w-5 h-5 group-hover:translate-x-1 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
              </svg>
            </button>
            <button className="px-8 py-4 bg-white/10 backdrop-blur-lg text-white rounded-2xl font-bold text-lg border-2 border-white/30 hover:bg-white/20 transform hover:-translate-y-1 transition-all duration-300 flex items-center justify-center gap-2">
              <span className="text-2xl">üñºÔ∏è</span>
              View Gallery
            </button>
          </div>

          {/* Stats */}
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 max-w-4xl mx-auto animate-fade-in-delay-4">
            {[
              { icon: "üìä", value: stats.totalImages, label: "Total Images" },
              { icon: "‚úÖ", value: stats.completedImages, label: "Completed" },
              { icon: "üëÅÔ∏è", value: stats.totalViews, label: "Views" },
              { icon: "üì•", value: stats.totalDownloads, label: "Downloads" }
            ].map((stat, i) => (
              <div key={i} className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 border border-white/20 hover:bg-white/15 transition-all duration-300 hover:scale-105">
                <div className="text-4xl mb-2">{stat.icon}</div>
                <div className="text-3xl font-black text-white mb-1">{stat.value.toLocaleString()}+</div>
                <div className="text-sm text-white/80 font-medium">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>

        {/* Scroll Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
          <svg className="w-6 h-6 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 14l-7 7m0 0l-7-7m7 7V3" />
          </svg>
        </div>
      </section>

      {/* Features Section */}
      <section id="section-features" className="py-24 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl sm:text-5xl font-black text-gray-900 mb-4">
              Powerful Features
              <span className="bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent"> at Your Fingertips</span>
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              Explore the capabilities of our multimodal AI platform
            </p>
          </div>

          <div className="grid md:grid-cols-3 gap-8">
            {features.map((feature, i) => (
              <div
                key={i}
                className={`group relative bg-gradient-to-br ${feature.bgGradient} rounded-3xl p-8 hover:shadow-2xl transition-all duration-500 hover:-translate-y-2 cursor-pointer overflow-hidden border border-gray-100`}
              >
                {/* Hover effect */}
                <div className={`absolute inset-0 bg-gradient-to-br ${feature.gradient} opacity-0 group-hover:opacity-10 transition-opacity duration-500`}></div>
                
                <div className={`relative w-20 h-20 bg-gradient-to-br ${feature.gradient} rounded-2xl flex items-center justify-center text-4xl mb-6 group-hover:scale-110 group-hover:rotate-3 transition-all duration-500 shadow-lg`}>
                  {feature.icon}
                </div>
                
                <h3 className="text-2xl font-bold text-gray-900 mb-3 group-hover:text-transparent group-hover:bg-gradient-to-r group-hover:from-indigo-600 group-hover:to-purple-600 group-hover:bg-clip-text transition-all duration-300">
                  {feature.title}
                </h3>
                
                <p className="text-gray-600 leading-relaxed mb-6">
                  {feature.description}
                </p>
                
                <button className={`inline-flex items-center gap-2 px-6 py-3 bg-gradient-to-r ${feature.gradient} text-white rounded-xl font-bold shadow-lg hover:shadow-xl transform group-hover:translate-x-1 transition-all duration-300`}>
                  Try Now
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                  </svg>
                </button>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* How It Works */}
      <section id="section-how" className="py-24 px-4 sm:px-6 lg:px-8 bg-gradient-to-b from-gray-50 to-white">
        <div className="max-w-6xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl sm:text-5xl font-black text-gray-900 mb-4">
              How It <span className="bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">Works</span>
            </h2>
            <p className="text-xl text-gray-600">Get started in three simple steps</p>
          </div>

          <div className="flex flex-col lg:flex-row items-center justify-center gap-8">
            {steps.map((step, i) => (
              <React.Fragment key={i}>
                <div className="relative bg-white rounded-3xl p-8 shadow-xl hover:shadow-2xl transition-all duration-300 hover:-translate-y-2 w-full lg:w-80 border border-gray-100">
                  <div className="absolute -top-4 -right-4 w-12 h-12 bg-gradient-to-br from-indigo-500 to-purple-600 rounded-full flex items-center justify-center text-white font-black text-xl shadow-lg">
                    {i + 1}
                  </div>
                  <div className="text-6xl mb-4 animate-float">{step.icon}</div>
                  <h3 className="text-2xl font-bold text-gray-900 mb-3">{step.title}</h3>
                  <p className="text-gray-600 leading-relaxed">{step.desc}</p>
                </div>
                {i < steps.length - 1 && (
                  <div className="hidden lg:block text-5xl text-indigo-600 font-bold animate-pulse">‚Üí</div>
                )}
              </React.Fragment>
            ))}
          </div>
        </div>
      </section>

      {/* Use Cases */}
      <section id="section-cases" className="py-24 px-4 sm:px-6 lg:px-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center mb-16">
            <h2 className="text-4xl sm:text-5xl font-black text-gray-900 mb-4">
              Perfect For <span className="bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent">Every Need</span>
            </h2>
            <p className="text-xl text-gray-600">Discover how VisionFusion AI can help you</p>
          </div>

          <div className="grid sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {useCases.map((useCase, i) => (
              <div
                key={i}
                className="bg-white rounded-2xl p-6 hover:shadow-xl transition-all duration-300 hover:-translate-y-2 cursor-pointer border border-gray-100 group"
              >
                <div className="text-5xl mb-4 group-hover:scale-110 transition-transform duration-300">{useCase.icon}</div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">{useCase.title}</h3>
