// frontend/src/components/TextToImage.jsx
import React, { useState, useEffect } from "react";
import "../App.css";

const TextToImage = () => {
  const [prompt, setPrompt] = useState("");
  const [negativePrompt, setNegativePrompt] = useState(
    "blurry, bad quality, distorted, ugly, low resolution"
  );
  const [generatedImage, setGeneratedImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [seed, setSeed] = useState("");
  const [usedSeed, setUsedSeed] = useState(null);
  const [showAdvanced, setShowAdvanced] = useState(false);
  const [generatedImages, setGeneratedImages] = useState([]);
  const [showGallery, setShowGallery] = useState(false);

  // Advanced settings
  const [numSteps, setNumSteps] = useState(30);
  const [guidanceScale, setGuidanceScale] = useState(7.5);
  const [width, setWidth] = useState(512);
  const [height, setHeight] = useState(512);

  // Preset prompts for inspiration
  const presetPrompts = [
    "A majestic dragon flying over a medieval castle, fantasy art, highly detailed",
    "A futuristic cyberpunk city at night with neon lights, cinematic lighting",
    "A serene Japanese garden with cherry blossoms, peaceful atmosphere, digital art",
    "An astronaut riding a horse on Mars, surreal, photorealistic",
    "A magical forest with glowing mushrooms, enchanted atmosphere, vibrant colors",
    "A steampunk airship floating among clouds, intricate details, golden hour lighting"
  ];

  useEffect(() => {
    fetchGeneratedImages();
  }, []);

  const fetchGeneratedImages = async () => {
    try {
      const res = await fetch("http://localhost:8000/generated-images/");
      if (!res.ok) throw new Error("Failed to fetch images");
      const data = await res.json();
      setGeneratedImages(data);
    } catch (err) {
      console.error("Error fetching generated images:", err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!prompt.trim()) {
      return alert("Please enter a prompt to generate an image.");
    }

    setLoading(true);
    setGeneratedImage(null);
    setUsedSeed(null);

    try {
      const requestBody = {
        prompt: prompt.trim(),
        negative_prompt: negativePrompt.trim(),
        num_inference_steps: numSteps,
        guidance_scale: guidanceScale,
        width: width,
        height: height,
        seed: seed ? parseInt(seed) : null,
      };

      const res = await fetch("http://localhost:8000/text-to-image/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(requestBody),
      });

      if (!res.ok) {
        const errorText = await res.text();
        throw new Error(`Server error: ${res.status} - ${errorText}`);
      }

      const data = await res.json();
      setGeneratedImage(data.image_data);
      setUsedSeed(data.seed);
      
      // Refresh gallery
      fetchGeneratedImages();
    } catch (err) {
      console.error(err);
      alert("Error generating image. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setPrompt("");
    setNegativePrompt("blurry, bad quality, distorted, ugly, low resolution");
    setGeneratedImage(null);
    setSeed("");
    setUsedSeed(null);
    setNumSteps(30);
    setGuidanceScale(7.5);
    setWidth(512);
    setHeight(512);
  };

  const handlePresetClick = (presetPrompt) => {
    setPrompt(presetPrompt);
  };

  const handleDownload = () => {
    if (!generatedImage) return;

    const link = document.createElement("a");
    link.href = generatedImage;
    link.download = `generated_image_${usedSeed || Date.now()}.png`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="container fade-in">
      <h1>üé® Text-to-Image Generation</h1>
      <p style={{ textAlign: "center", color: "#6b7280", marginBottom: "2rem" }}>
        Transform your imagination into stunning visuals with AI
      </p>

      <form onSubmit={handleSubmit}>
        {/* Main Prompt Input */}
        <div>
          <label
            style={{
              display: "block",
              marginBottom: "0.5rem",
              fontWeight: "600",
              color: "#374151",
            }}
          >
            ‚ú® Describe your image
          </label>
          <textarea
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            placeholder="E.g., A beautiful landscape with mountains and a lake at sunset, digital art, highly detailed"
            style={{
              width: "100%",
              minHeight: "100px",
              padding: "0.875rem",
              fontSize: "1rem",
              border: "2px solid #e5e7eb",
              borderRadius: "12px",
              resize: "vertical",
              fontFamily: "inherit",
              transition: "all 0.3s ease",
            }}
            onFocus={(e) => {
              e.target.style.borderColor = "#6366f1";
              e.target.style.boxShadow = "0 0 0 3px #eef2ff";
            }}
            onBlur={(e) => {
              e.target.style.borderColor = "#e5e7eb";
              e.target.style.boxShadow = "none";
            }}
          />
        </div>

        {/* Preset Prompts */}
        <div>
          <label
            style={{
              display: "block",
              marginBottom: "0.5rem",
              fontWeight: "600",
              color: "#374151",
            }}
          >
            üí° Or try these ideas
          </label>
          <div
            style={{
              display: "flex",
              flexWrap: "wrap",
              gap: "0.5rem",
            }}
          >
            {presetPrompts.map((preset, index) => (
              <button
                key={index}
                type="button"
                onClick={() => handlePresetClick(preset)}
                style={{
                  padding: "0.5rem 1rem",
                  fontSize: "0.875rem",
                  background: "linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)",
                  color: "#374151",
                  border: "1px solid #d1d5db",
                  borderRadius: "9999px",
                  cursor: "pointer",
                  transition: "all 0.3s ease",
                  fontWeight: "500",
                }}
                onMouseOver={(e) => {
                  e.target.style.background =
                    "linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)";
                  e.target.style.color = "white";
                  e.target.style.transform = "translateY(-2px)";
                }}
                onMouseOut={(e) => {
                  e.target.style.background =
                    "linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)";
                  e.target.style.color = "#374151";
                  e.target.style.transform = "translateY(0)";
                }}
              >
                {preset.substring(0, 40)}...
              </button>
            ))}
          </div>
        </div>

        {/* Advanced Settings Toggle */}
        <button
          type="button"
          onClick={() => setShowAdvanced(!showAdvanced)}
          style={{
            background: "linear-gradient(135deg, #6b7280 0%, #4b5563 100%)",
            padding: "0.75rem 1.25rem",
            width: "100%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            gap: "0.5rem",
          }}
        >
          {showAdvanced ? "‚ñº" : "‚ñ∂"} Advanced Settings
        </button>

        {/* Advanced Settings Panel */}
        {showAdvanced && (
          <div
            style={{
              padding: "1.5rem",
              backgroundColor: "#f9fafb",
              borderRadius: "12px",
              border: "1px solid #e5e7eb",
              animation: "fadeIn 0.3s ease",
            }}
          >
            <div style={{ marginBottom: "1rem" }}>
              <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                üö´ Negative Prompt (what to avoid)
              </label>
              <input
                type="text"
                value={negativePrompt}
                onChange={(e) => setNegativePrompt(e.target.value)}
                style={{ width: "100%" }}
              />
            </div>

            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "1rem" }}>
              <div>
                <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                  Steps: {numSteps}
                </label>
                <input
                  type="range"
                  min="10"
                  max="50"
                  value={numSteps}
                  onChange={(e) => setNumSteps(parseInt(e.target.value))}
                  style={{ width: "100%" }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                  Guidance: {guidanceScale}
                </label>
                <input
                  type="range"
                  min="1"
                  max="20"
                  step="0.5"
                  value={guidanceScale}
                  onChange={(e) => setGuidanceScale(parseFloat(e.target.value))}
                  style={{ width: "100%" }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                  Width: {width}px
                </label>
                <select
                  value={width}
                  onChange={(e) => setWidth(parseInt(e.target.value))}
                  style={{ width: "100%", padding: "0.5rem", borderRadius: "6px" }}
                >
                  <option value="512">512</option>
                  <option value="768">768</option>
                  <option value="1024">1024</option>
                </select>
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                  Height: {height}px
                </label>
                <select
                  value={height}
                  onChange={(e) => setHeight(parseInt(e.target.value))}
                  style={{ width: "100%", padding: "0.5rem", borderRadius: "6px" }}
                >
                  <option value="512">512</option>
                  <option value="768">768</option>
                  <option value="1024">1024</option>
                </select>
              </div>
            </div>

            <div style={{ marginTop: "1rem" }}>
              <label style={{ display: "block", marginBottom: "0.5rem", fontWeight: "600" }}>
                üé≤ Seed (optional, for reproducibility)
              </label>
              <input
                type="number"
                value={seed}
                onChange={(e) => setSeed(e.target.value)}
                placeholder="Leave empty for random"
                style={{ width: "100%" }}
              />
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div style={{ display: "flex", gap: "1rem" }}>
          <button type="submit" disabled={loading || !prompt.trim()} style={{ flex: 1 }}>
            {loading ? "üé® Creating Magic..." : "üöÄ Generate Image"}
          </button>
          {(prompt || generatedImage) && (
            <button
              type="button"
              onClick={handleReset}
              style={{
                background: "linear-gradient(135deg, #6b7280 0%, #4b5563 100%)",
                flex: "0 0 auto",
                padding: "0.875rem 1.25rem",
              }}
            >
              Reset
            </button>
          )}
        </div>
      </form>

      {/* Loading Animation */}
      {loading && (
        <div className="loader-container">
          <div className="loader"></div>
          <p>üé® AI is painting your masterpiece...</p>
          <p style={{ fontSize: "0.875rem", color: "#9ca3af", marginTop: "0.5rem" }}>
            This may take 30-60 seconds depending on settings
          </p>
        </div>
      )}

      {/* Generated Image Display */}
      {!loading && generatedImage && (
        <div className="result-card">
          <div style={{ textAlign: "center" }}>
            <img
              src={generatedImage}
              alt="Generated"
              style={{
                maxWidth: "100%",
                borderRadius: "12px",
                boxShadow: "0 10px 15px -3px rgba(0, 0, 0, 0.1)",
                marginBottom: "1rem",
              }}
            />
            <div
              style={{
                display: "flex",
                gap: "0.5rem",
                justifyContent: "center",
                flexWrap: "wrap",
              }}
            >
              <button
                onClick={handleDownload}
                style={{
                  background: "linear-gradient(135deg, #10b981 0%, #059669 100%)",
                  padding: "0.75rem 1.5rem",
                }}
              >
                üì• Download Image
              </button>
              {usedSeed && (
                <div
                  style={{
                    padding: "0.75rem 1.5rem",
                    backgroundColor: "#f3f4f6",
                    borderRadius: "8px",
                    color: "#374151",
                    fontWeight: "600",
                    display: "flex",
                    alignItems: "center",
                    gap: "0.5rem",
                  }}
                >
                  üé≤ Seed: {usedSeed}
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Gallery Button */}
      <div style={{ marginTop: "2rem", textAlign: "center" }}>
        <button
          type="button"
          onClick={() => setShowGallery(!showGallery)}
          style={{
            background: "linear-gradient(135deg, #8b5cf6 0%, #7c3aed 100%)",
            padding: "0.875rem 1.5rem",
          }}
        >
          {showGallery ? "Hide" : "Show"} Gallery ({generatedImages.length})
        </button>
      </div>

      {/* Generated Images Gallery */}
      {showGallery && generatedImages.length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>
            üñºÔ∏è Your Generated Images
          </h2>
          <div className="images-container">
            {generatedImages.map((img, index) => (
              <div key={index} className="image-card">
                <img src={img.image_data} alt={img.prompt} />
                <div className="image-info">
                  <strong>{img.prompt.substring(0, 50)}...</strong>
                  <p style={{ fontSize: "0.8rem", color: "#6b7280" }}>
                    üé≤ Seed: {img.seed}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Tips Section */}
      {!loading && !generatedImage && (
        <div
          style={{
            marginTop: "2rem",
            padding: "1.5rem",
            backgroundColor: "#fef3c7",
            borderRadius: "12px",
            border: "1px solid #fde68a",
          }}
        >
          <h3 style={{ color: "#92400e", marginBottom: "0.75rem", fontSize: "1.1rem" }}>
            üí° Tips for Amazing Results
          </h3>
          <ul style={{ color: "#78350f", paddingLeft: "1.5rem", lineHeight: "1.8" }}>
            <li>Be specific and descriptive in your prompts</li>
            <li>Include art style keywords like "digital art", "oil painting", "photorealistic"</li>
            <li>Add quality descriptors: "highly detailed", "8k", "cinematic lighting"</li>
            <li>Use negative prompts to avoid unwanted elements</li>
            <li>Higher steps = better quality but slower generation</li>
            <li>Guidance scale 7-9 usually works best</li>
          </ul>
        </div>
      )}
    </div>
  );
};

export default TextToImage;
