import React, { useState, useRef } from "react";

// Helper function to format file size
const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
};

const EnhancedVQAForm = () => {
    const [file, setFile] = useState(null);
    const [preview, setPreview] = useState(null);
    const [question, setQuestion] = useState("");
    const [answer, setAnswer] = useState("");
    const [confidence, setConfidence] = useState(null);
    const [loading, setLoading] = useState(false);
    const [fromCache, setFromCache] = useState(false);
    const [fileName, setFileName] = useState("");
    const [dragActive, setDragActive] = useState(false);

    // Image details state
    const [imageDetails, setImageDetails] = useState({
        width: null,
        height: null,
        size: null,
        type: null,
    });

    // Reference to the main form container to get its height
    const formRef = useRef(null);

    // Sample questions for quick access
    const quickQuestions = [
        "What is in this image?",
        "Describe the main objects",
        "What colors are prominent?",
        "What is the setting or location?",
        "How many people are visible?",
        "What activity is happening?"
    ];

    const processFile = (selectedFile) => {
        setFile(selectedFile);
        setFileName(selectedFile?.name || "");

        if (selectedFile) {
            // 1. Set file size and type
            setImageDetails(prev => ({
                ...prev,
                size: formatFileSize(selectedFile.size),
                type: selectedFile.type,
            }));

            // 2. Read file for preview
            const reader = new FileReader();
            reader.onloadend = () => setPreview(reader.result);
            reader.readAsDataURL(selectedFile);

            // 3. Read image for dimensions (width/height)
            const img = new Image();
            img.onload = () => {
                setImageDetails(prev => ({
                    ...prev,
                    width: img.naturalWidth,
                    height: img.naturalHeight
                }));
            };
            img.src = URL.createObjectURL(selectedFile);

        } else {
            setPreview(null);
            setFileName("");
            setImageDetails({ width: null, height: null, size: null, type: null });
        }
    };

    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        if (selectedFile) {
            processFile(selectedFile);
        }
    };

    const handleDrag = (e) => {
        e.preventDefault();
        e.stopPropagation();
        if (e.type === "dragenter" || e.type === "dragover") {
            setDragActive(true);
        } else if (e.type === "dragleave") {
            setDragActive(false);
        }
    };

    const handleDrop = (e) => {
        e.preventDefault();
        e.stopPropagation();
        setDragActive(false);

        if (e.dataTransfer.files && e.dataTransfer.files[0]) {
            processFile(e.dataTransfer.files[0]);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!file || !question) {
            return alert("Please upload an image and enter a question.");
        }

        const formData = new FormData();
        formData.append("image", file);
        formData.append("question", question);

        setLoading(true);
        setAnswer("");
        setConfidence(null);
        setFromCache(false);

        try {
            const res = await fetch("http://localhost:8000/vqa/", {
                method: "POST",
                body: formData,
            });

            if (!res.ok) {
                const text = await res.text();
                throw new Error(`Server error: ${res.status} - ${text}`);
            }

            const data = await res.json();
            setAnswer(data.answer);
            setConfidence(data.confidence ?? null);
            setFromCache(data.from_cache || false);
        } catch (err) {
            console.error(err);
            setAnswer("Error processing request. Please try again.");
            setConfidence(null);
        } finally {
            setLoading(false);
        }
    };

    const handleReset = () => {
        setFile(null);
        setPreview(null);
        setQuestion("");
        setAnswer("");
        setConfidence(null);
        setFromCache(false);
        setFileName("");
        setImageDetails({ width: null, height: null, size: null, type: null });
    };

    const getConfidenceLevel = (conf) => {
        if (conf >= 0.8) return "high";
        if (conf >= 0.5) return "medium";
        return "low";
    };

    const getConfidenceEmoji = (conf) => {
        if (conf >= 0.8) return "‚úì";
        if (conf >= 0.5) return "~";
        return "!";
    };

    // Calculate height for the image-show container to match the form container
    const imageShowHeight = formRef.current ? `${formRef.current.offsetHeight}px` : 'auto';

    return (
        <div className="app-wrapper">
            <div className="container fade-in" ref={formRef}>

                <h1>üîç Visual Question Answering</h1>
                <p style={{ textAlign: "center", color: "var(--text-secondary)", marginBottom: "2rem", fontSize: "1.05rem" }}>
                    Upload an image and ask questions about it ‚Äî powered by AI
                </p>

                <form onSubmit={handleSubmit}>
                    {/* Enhanced File Input with Drag & Drop */}
                    <div
                        className="file-input-wrapper"
                        onDragEnter={handleDrag}
                        onDragLeave={handleDrag}
                        onDragOver={handleDrag}
                        onDrop={handleDrop}
                        style={{
                            border: dragActive ? "3px dashed var(--primary-color)" : undefined,
                            backgroundColor: dragActive ? "var(--primary-light)" : undefined
                        }}
                    >
                        <input
                            type="file"
                            id="file-upload"
                            accept="image/*"
                            onChange={handleFileChange}
                        />
                        <label htmlFor="file-upload" className="file-input-label">
                            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                            </svg>
                            {fileName ? (
                                <span style={{ display: "flex", flexDirection: "column", alignItems: "center", gap: "0.25rem" }}>
                                    <strong>{fileName}</strong>
                                    <span style={{ fontSize: "0.875rem", opacity: 0.7 }}>Click to change</span>
                                </span>
                            ) : (
                                <span>
                                    {dragActive ? "Drop your image here" : "Choose an image or drag & drop"}
                                </span>
                            )}
                        </label>
                    </div>

                    {/* Question Input */}
                    <div>
                        <label style={{
                            display: "block",
                            marginBottom: "0.5rem",
                            fontWeight: "600",
                            color: "var(--text-primary)",
                            fontSize: "1rem"
                        }}>
                            üí¨ Your Question
                        </label>
                        <textarea
                            placeholder="What would you like to know about this image?"
                            value={question}
                            onChange={(e) => setQuestion(e.target.value)}
                            style={{
                                width: "100%",
                                minHeight: "100px",
                                padding: "1rem",
                                fontSize: "1rem",
                                border: "2px solid var(--border-color)",
                                borderRadius: "var(--radius-md)",
                                resize: "vertical",
                                fontFamily: "inherit",
                                transition: "all var(--transition-base)",
                                backgroundColor: "var(--bg-primary)"
                            }}
                            onFocus={(e) => {
                                e.target.style.borderColor = "var(--primary-color)";
                                e.target.style.boxShadow = "0 0 0 4px var(--primary-light)";
                            }}
                            onBlur={(e) => {
                                e.target.style.borderColor = "var(--border-color)";
                                e.target.style.boxShadow = "none";
                            }}
                        />
                    </div>

                    {/* Quick Question Suggestions */}
                    {!question && (
                        <div>
                            <label style={{
                                display: "block",
                                marginBottom: "0.5rem",
                                fontWeight: "600",
                                color: "var(--text-primary)",
                                fontSize: "0.95rem"
                            }}>
                                üí° Quick questions
                            </label>
                            <div style={{
                                display: "flex",
                                flexWrap: "wrap",
                                gap: "0.5rem"
                            }}>
                                {quickQuestions.map((q, idx) => (
                                    <button
                                        key={idx}
                                        type="button"
                                        onClick={() => setQuestion(q)}
                                        style={{
                                            padding: "0.5rem 1rem",
                                            fontSize: "0.875rem",
                                            background: "linear-gradient(135deg, var(--bg-tertiary) 0%, #e5e7eb 100%)",
                                            color: "var(--text-primary)",
                                            border: "1px solid var(--border-color)",
                                            borderRadius: "9999px",
                                            cursor: "pointer",
                                            transition: "all var(--transition-base)",
                                            fontWeight: "500"
                                        }}
                                        onMouseEnter={(e) => {
                                            e.target.style.background = "linear-gradient(135deg, var(--primary-color) 0%, var(--primary-hover) 100%)";
                                            e.target.style.color = "white";
                                            e.target.style.transform = "translateY(-2px)";
                                            e.target.style.boxShadow = "var(--shadow-md)";
                                        }}
                                        onMouseLeave={(e) => {
                                            e.target.style.background = "linear-gradient(135deg, var(--bg-tertiary) 0%, #e5e7eb 100%)";
                                            e.target.style.color = "var(--text-primary)";
                                            e.target.style.transform = "translateY(0)";
                                            e.target.style.boxShadow = "none";
                                        }}
                                    >
                                        {q}
                                    </button>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* Action Buttons */}
                    <div style={{ display: "flex", gap: "1rem", marginTop: "0.5rem" }}>
                        <button
                            type="submit"
                            disabled={loading || !file || !question}
                            style={{
                                flex: 1,
                                opacity: (loading || !file || !question) ? 0.5 : 1
                            }}
                        >
                            {loading ? (
                                <span style={{ display: "flex", alignItems: "center", justifyContent: "center", gap: "0.5rem" }}>
                                    <span style={{
                                        border: "2px solid rgba(255,255,255,0.3)",
                                        borderTop: "2px solid white",
                                        borderRadius: "50%",
                                        width: "16px",
                                        height: "16px",
                                        animation: "spin 0.8s linear infinite"
                                    }}></span>
                                    Processing...
                                </span>
                            ) : "üöÄ Ask Question"}
                        </button>
                        {(file || question) && (
                            <button
                                type="button"
                                onClick={handleReset}
                                style={{
                                    background: "linear-gradient(135deg, var(--text-secondary) 0%, #4b5563 100%)",
                                    flex: "0 0 auto",
                                    padding: "0.875rem 1.5rem"
                                }}
                            >
                                üîÑ Reset
                            </button>
                        )}
                    </div>
                </form>

                {/* Loader */}
                {loading && (
                    <div className="loader-container">
                        <div className="loader"></div>
                        <p>ü§ñ AI is analyzing your image...</p>
                        <p style={{ fontSize: "0.9rem", color: "var(--text-secondary)", marginTop: "0.5rem" }}>
                            This usually takes 5-10 seconds
                        </p>
                    </div>
                )}

                {/* Answer Display */}
                {!loading && answer && (
                    <div className="result-card">
                        {fromCache && (
                            <div style={{
                                backgroundColor: "#fef3c7",
                                color: "#92400e",
                                padding: "0.75rem 1rem",
                                borderRadius: "8px",
                                fontSize: "0.9rem",
                                marginBottom: "1.25rem",
                                display: "flex",
                                alignItems: "center",
                                gap: "0.5rem",
                                fontWeight: "600",
                                border: "1px solid #fde68a"
                            }}>
                                ‚ö° Retrieved from cache (instant result!)
                            </div>
                        )}

                        <div style={{
                            display: "flex",
                            alignItems: "start",
                            gap: "1rem",
                            marginBottom: "1rem"
                        }}>
                            <div style={{
                                fontSize: "2rem",
                                flexShrink: 0
                            }}>
                                üí¨
                            </div>
                            <div className="answer-text">{answer}</div>
                        </div>

                        {confidence !== null && (
                            <div className="confidence-container">
                                <span className={`confidence-badge ${getConfidenceLevel(confidence)}`}>
                                    {getConfidenceEmoji(confidence)} {(confidence * 100).toFixed(1)}% Confident
                                </span>
                            </div>
                        )}

                        {/* Action buttons for result */}
                        <div style={{
                            marginTop: "1.5rem",
                            paddingTop: "1.5rem",
                            borderTop: "1px solid var(--border-color)",
                            display: "flex",
                            gap: "0.75rem",
                            justifyContent: "center"
                        }}>
                            <button
                                type="button"
                                onClick={handleReset}
                                style={{
                                    background: "linear-gradient(135deg, var(--primary-color) 0%, var(--primary-hover) 100%)",
                                    padding: "0.75rem 1.5rem",
                                    fontSize: "0.9rem"
                                }}
                            >
                                Ask Another Question
                            </button>
                        </div>
                    </div>
                )}

                {/* Helpful Tips */}
                {!preview && !loading && !answer && (
                    <div style={{
                        marginTop: "2rem",
                        padding: "1.75rem",
                        background: "linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)",
                        borderRadius: "var(--radius-md)",
                        border: "2px solid #bae6fd"
                    }}>
                        <h3 style={{
                            color: "#0c4a6e",
                            marginBottom: "1rem",
                            fontSize: "1.15rem",
                            display: "flex",
                            alignItems: "center",
                            gap: "0.5rem"
                        }}>
                            üí° Tips for Better Results
                        </h3>
                        <ul style={{
                            color: "#0e7490",
                            paddingLeft: "1.5rem",
                            lineHeight: "1.9",
                            margin: 0
                        }}>
                            <li><strong>Clear images work best:</strong> Use well-lit, focused photos</li>
                            <li><strong>Be specific:</strong> Ask direct questions about visible elements</li>
                            <li><strong>Examples:</strong> "What color is the car?" or "How many dogs are in the image?"</li>
                            <li><strong>Avoid abstract questions:</strong> Focus on what can be seen</li>
                        </ul>
                    </div>
                )}


                <style>{`
            @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
            }
        `}</style>
            </div>

            {/* Image Show Section - Only render if 'preview' exists */}
            {preview && (
                <div 
                    className="image-show" 
                    style={{ 
                        height: imageShowHeight,
                        minHeight: '400px', // Ensure min height even if form is small
                    }}
                >
                    <div className="image-preview-wrapper">
                        <img
                            src={preview}
                            alt="Preview"
                            // Dimensions are handled by CSS classes for object-fit and max-height/width
                        />
                    </div>

                    <div className="image-details-card">
                        <h3 style={{
                            fontWeight: 600,
                            fontSize: '1.1rem',
                            color: 'var(--text-primary)',
                            marginBottom: '0.75rem'
                        }}>
                            üñºÔ∏è Image Details
                        </h3>
                        <div className="details-grid">
                            <strong>File Name:</strong>
                            <p>{fileName}</p>
                            
                            <strong>File Type:</strong>
                            <p>{imageDetails.type || 'N/A'}</p>
                            
                            <strong>Dimensions:</strong>
                            <p>
                                {imageDetails.width ? `${imageDetails.width} x ${imageDetails.height} px` : 'Loading...'}
                            </p>

                            <strong>Size:</strong>
                            <p>{imageDetails.size || 'N/A'}</p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default EnhancedVQAForm;
