import React, { useState } from "react";

const EnhancedVQAForm = () => {
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [confidence, setConfidence] = useState(null);
  const [loading, setLoading] = useState(false);
  const [fromCache, setFromCache] = useState(false);
  const [fileName, setFileName] = useState("");
  const [dragActive, setDragActive] = useState(false);

  const quickQuestions = [
    "What is in this image?",
    "Describe the main objects",
    "What colors are prominent?",
    "What is the setting or location?",
    "How many people are visible?",
    "What activity is happening?"
  ];

  const processFile = (selectedFile) => {
    setFile(selectedFile);
    setFileName(selectedFile?.name || "");

    if (selectedFile) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(selectedFile);
    } else {
      setPreview(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file || !question) return alert("Upload image & enter question");

    const formData = new FormData();
    formData.append("image", file);
    formData.append("question", question);

    setLoading(true);
    setAnswer("");
    setConfidence(null);

    try {
      const res = await fetch("http://localhost:8000/vqa/", {
        method: "POST",
        body: formData
      });

      const data = await res.json();
      setAnswer(data.answer);
      setConfidence(data.confidence ?? null);
      setFromCache(data.from_cache || false);
    } catch {
      setAnswer("Error processing request.");
    } finally {
      setLoading(false);
    }
  };

  const handleReset = () => {
    setFile(null);
    setPreview(null);
    setQuestion("");
    setAnswer("");
    setConfidence(null);
    setFromCache(false);
    setFileName("");
  };

  const getConfidenceLevel = (c) => (c >= 0.8 ? "high" : c >= 0.5 ? "medium" : "low");

  return (
    <div className="container">
      <h1>üîç Visual Question Answering</h1>

      {/* MAIN LAYOUT */}
      <div className="vqa-layout">
        {/* LEFT SIDE */}
        <div className="vqa-form">
          <form onSubmit={handleSubmit}>
            {/* Upload */}
            <div className="file-input-wrapper">
              <input type="file" accept="image/*" onChange={(e) => processFile(e.target.files[0])} />
              <label>{fileName || "Choose image or drag & drop"}</label>
            </div>

            {/* Question */}
            <textarea
              placeholder="Ask something about the image..."
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
            />

            {/* Quick Questions */}
            {!question && (
              <div className="quick-questions">
                {quickQuestions.map((q, i) => (
                  <button key={i} type="button" onClick={() => setQuestion(q)}>
                    {q}
                  </button>
                ))}
              </div>
            )}

            {/* Buttons */}
            <button disabled={loading || !file || !question}>
              {loading ? "Processing..." : "üöÄ Ask Question"}
            </button>
          </form>
        </div>

        {/* RIGHT SIDE PREVIEW */}
        <div className="vqa-preview">
          {preview ? (
            <div className="image-preview">
              <img src={preview} alt="Preview" />
              <p>‚úì Image loaded</p>
            </div>
          ) : (
            <div className="preview-placeholder">
              üì∑ Image preview will appear here
            </div>
          )}
        </div>
      </div>

      {/* ANSWER */}
      {answer && !loading && (
        <div className="result-card">
          <p>{answer}</p>
          {confidence !== null && (
            <span className={`confidence ${getConfidenceLevel(confidence)}`}>
              {(confidence * 100).toFixed(1)}%
            </span>
          )}
        </div>
      )}

      {/* STYLES */}
      <style>{`
        .vqa-layout {
          display: flex;
          gap: 2rem;
          margin-top: 2rem;
        }

        .vqa-form {
          flex: 2;
        }

        .vqa-preview {
          flex: 1;
          position: sticky;
          top: 1rem;
        }

        .image-preview {
          padding: 1rem;
          border: 2px dashed #e5e7eb;
          border-radius: 12px;
          background: #f9fafb;
          text-align: center;
        }

        .image-preview img {
          max-width: 100%;
          max-height: 300px;
          border-radius: 8px;
        }

        .preview-placeholder {
          height: 300px;
          border: 2px dashed #d1d5db;
          border-radius: 12px;
          display: flex;
          align-items: center;
          justify-content: center;
          color: #9ca3af;
        }

        textarea {
          width: 100%;
          min-height: 100px;
          margin: 1rem 0;
        }

        @media (max-width: 768px) {
          .vqa-layout {
            flex-direction: column;
          }

          .vqa-preview {
            position: static;
          }
        }
      `}</style>
    </div>
  );
};

export default EnhancedVQAForm;
