import React, { useState } from "react";
import axios from "axios";

function VQAForm() {
  const [file, setFile] = useState(null);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("image", file);
    formData.append("question", question);

    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/vqa/`, formData);
      setAnswer(res.data.answer);
    } catch (err) {
      console.error(err);
      setAnswer("Error: Could not get answer.");
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
        <input type="text" placeholder="Ask a question..." onChange={(e) => setQuestion(e.target.value)} required />
        <button type="submit">Ask</button>
      </form>
      {answer && <p><strong>Answer:</strong> {answer}</p>}
    </div>
  );
}

export default VQAForm;
