import React, { useState } from "react";

const Home = () => {
    const [image, setImage] = useState(null);
    const [textPrompt, setTextPrompt] = useState("");
    const [generatedImage, setGeneratedImage] = useState(null);
    const [question, setQuestion] = useState("");
    const [answer, setAnswer] = useState("");
    const [loading, setLoading] = useState(false);

    // Handle text-to-image generation
    const handleGenerateImage = async (e) => {
        e.preventDefault();
        setLoading(true);
        setGeneratedImage(null);
        // Replace with your backend API endpoint
        const response = await fetch("/api/generate-image", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ prompt: textPrompt }),
        });
        const data = await response.json();
        setGeneratedImage(data.imageUrl);
        setLoading(false);
    };

    // Handle image upload
    const handleImageUpload = (e) => {
        setImage(e.target.files[0]);
        setAnswer("");
    };

    // Handle image-to-question-answer
    const handleAskQuestion = async (e) => {
        e.preventDefault();
        if (!image || !question) return;
        setLoading(true);
        setAnswer("");
        const formData = new FormData();
        formData.append("image", image);
        formData.append("question", question);

        // Replace with your backend API endpoint
        const response = await fetch("/api/answer-question", {
            method: "POST",
            body: formData,
        });
        const data = await response.json();
        setAnswer(data.answer);
        setLoading(false);
    };

    return (
        <div style={{ maxWidth: 600, margin: "0 auto", padding: 24 }}>
            <h2>Text to Image</h2>
            <form onSubmit={handleGenerateImage} style={{ marginBottom: 32 }}>
                <input
                    type="text"
                    placeholder="Enter a prompt to generate an image"
                    value={textPrompt}
                    onChange={(e) => setTextPrompt(e.target.value)}
                    style={{ width: "70%", marginRight: 8 }}
                />
                <button type="submit" disabled={loading || !textPrompt}>
                    Generate
                </button>
            </form>
            {loading && <p>Processing...</p>}
            {generatedImage && (
                <div>
                    <img
                        src={generatedImage}
                        alt="Generated"
                        style={{ maxWidth: "100%", border: "1px solid #ccc" }}
                    />
                </div>
            )}

            <hr style={{ margin: "40px 0" }} />

            <h2>Image to Question Answer</h2>
            <form onSubmit={handleAskQuestion}>
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleImageUpload}
                    style={{ marginBottom: 8 }}
                />
                <br />
                <input
                    type="text"
                    placeholder="Ask a question about the image"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    style={{ width: "70%", marginRight: 8, marginTop: 8 }}
                />
                <button type="submit" disabled={loading || !image || !question}>
                    Ask
                </button>
            </form>
            {answer && (
                <div style={{ marginTop: 16 }}>
                    <strong>Answer:</strong> {answer}
                </div>
            )}
        </div>
    );
};

export default Home;
