import React, { useState } from "react";
import "../App.css";

const VQAForm = () => {
  const [file, setFile] = useState(null);
  const [question, setQuestion] = useState("");
  const [answerData, setAnswerData] = useState(null); // { answer, confidence }

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file || !question) {
      return alert("Please upload an image and enter a question.");
    }

    const formData = new FormData();
    formData.append("image", file);
    formData.append("question", question);

    try {
      const res = await fetch("http://localhost:8000/vqa/", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`Server error: ${res.status} - ${text}`);
      }

      const data = await res.json();

      // Display confidence as percentage
      const confidencePercent = data.confidence
        ? Math.round(data.confidence * 100)
        : null;

      setAnswerData({
        answer: data.answer,
        confidence: confidencePercent,
      });
    } catch (err) {
      console.error(err);
      setAnswerData({ answer: "Error processing request", confidence: null });
    }
  };

  return (
    <div className="container">
      <h1>Visual Question Answering</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFile(e.target.files[0])}
        />
        <input
          type="text"
          placeholder="Enter your question"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
        />
        <button type="submit">Ask</button>
      </form>

      {answerData && (
        <div className="result-box">
          <p className="answer">{answerData.answer}</p>
          {answerData.confidence !== null && (
            <span className="confidence-badge">
              Sure of it: {answerData.confidence}%
            </span>
          )}
        </div>
      )}
    </div>
  );
};

export default VQAForm;
